package operators;

public class Assignment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 5;
		int var;
		var = i;
		System.out.println("var =" +var);
		var += i;
		System.out.println("var +=" +var);
		var -= i;
		System.out.println("var -=" +var);
		var *= i;
		System.out.println("var *=" +var);
		var /= i;
		System.out.println("var /=" +var);
		var %= i;
		System.out.println("var %=" +var);

	}

}
